#pragma once#include <vector>#include "mass.h"class Triangle {public:    Triangle(Mass* _m1, Mass* _m2, Mass* _m3) : m1(_m1), m2(_m2), m3(_m3) {};    Mass *m1, *m2, *m3;    SharedEdge* sharedEdge;};class Edge {public:    SharedEdge* sharedEdge;};class SharedEdge {public:    Edge* edge1;    SharedEdge* edge2;    SharedEdge* next;    Triangle* triangle;    Mass* mass;};class Mesh {public:    std::vector<Triangle*> triangles;};