#pragma once#include <vector>#include <cmath>#include "glm/glm.hpp"#include "cloth.h"class Sphere {public:    Sphere(glm::vec3 _center, float _radius, int _stacks, int _sectors, float _friction = 0.01) : center(_center), radius(_radius), stacks(_stacks), sectors(_sectors), friction(_friction) {        buildVertices();    }    void buildVertices();    void collideCloth(Mass& m);    public:    glm::vec3 center;    int stacks, sectors;    float radius;    float friction;    std::vector<float> positions;    std::vector<float> normals;    std::vector<unsigned int> indices;};