#pragma once#include "mass.h"enum SPRING_TYPE {    STRUCTUAL = 0,    SHEARING = 1,    BENDING = 2};struct Spring {    Spring(Mass* a, Mass* b, SPRING_TYPE t) : ma(a), mb(b), spring_type(t), rest_length(length(mb->position - ma->position)) {};    Spring(Mass* a, Mass* b, float l) : ma(a), mb(b), rest_length(l) {};    public:    Mass* ma;    Mass* mb;    SPRING_TYPE spring_type;    float rest_length;};